package db

import (
	"bytes"
	"encoding/hex"
	"sync"
	"testing"
	"time"

	"github.com/OpenBazaar/openbazaar-go/repo"
	"github.com/OpenBazaar/openbazaar-go/schema"
	"github.com/OpenBazaar/wallet-interface"
	"github.com/btcsuite/btcd/wire"
)

func buildNewTransactionStore() (repo.TransactionStore, func(), error) {
	appSchema := schema.MustNewCustomSchemaManager(schema.SchemaContext{
		DataPath:        schema.GenerateTempPath(),
		TestModeEnabled: true,
	})

	if err := appSchema.BuildSchemaDirectories(); err != nil {
		return nil, nil, err
	}
	if err := appSchema.InitializeDatabase(); err != nil {
		return nil, nil, err
	}
	database, err := appSchema.OpenDatabase()
	if err != nil {
		return nil, nil, err
	}
	return NewTransactionStore(database, new(sync.Mutex), wallet.Bitcoin), appSchema.DestroySchemaDirectories, nil
}

func TestTxnsPut(t *testing.T) {
	var txdb, teardown, err = buildNewTransactionStore()
	if err != nil {
		t.Fatal(err)
	}
	defer teardown()

	tx := wire.NewMsgTx(wire.TxVersion)
	txHex := ""
	raw, _ := hex.DecodeString(txHex)
	r := bytes.NewReader(raw)
	err = tx.Deserialize(r)
	if err != nil {
		t.Log(err)
	}

	err = txdb.Put(raw, tx.TxHash().String(), "5", 1, time.Now(), false)
	if err != nil {
		t.Error(err)
	}
	stmt, err := txdb.PrepareQuery("select tx, value, height, watchOnly from txns where txid=?")
	if err != nil {
		t.Error(err)
	}
	defer stmt.Close()
	var ret []byte
	var val int
	var height int
	var watchOnly int
	err = stmt.QueryRow(tx.TxHash().String()).Scan(&ret, &val, &height, &watchOnly)
	if err != nil {
		t.Error(err)
	}
	if hex.EncodeToString(ret) != txHex {
		t.Error("Txns db put failed")
	}
	if val != 5 {
		t.Error("Txns db failed to put value")
	}
	if height != 1 {
		t.Error("Txns db failed to put height")
	}
	if watchOnly != 0 {
		t.Error("Txns db failed to put watchOnly")
	}
}

func TestTxnsGet(t *testing.T) {
	var txdb, teardown, err = buildNewTransactionStore()
	if err != nil {
		t.Fatal(err)
	}
	defer teardown()

	tx := wire.NewMsgTx(wire.TxVersion)
	txHex := ""
	raw, _ := hex.DecodeString(txHex)
	r := bytes.NewReader(raw)
	err = tx.Deserialize(r)
	if err != nil {
		t.Log(err)
	}

	now := time.Now()
	err = txdb.Put(raw, tx.TxHash().String(), "0", 1, now, false)
	if err != nil {
		t.Error(err)
	}
	txn, err := txdb.Get(tx.TxHash().String())
	if err != nil {
		t.Error(err)
	}
	tx2 := wire.NewMsgTx(wire.TxVersion)
	err = tx2.Deserialize(bytes.NewReader(txn.Bytes))
	if err != nil {
		t.Log(err)
	}
	if tx.TxHash().String() != tx2.TxHash().String() {
		t.Error("Txn db get failed")
	}
	if txn.Height != 1 {
		t.Error("Txn db failed to get height")
	}
	if now.Equal(txn.Timestamp) {
		t.Error("Txn db failed to return correct time")
	}
	if txn.WatchOnly {
		t.Error("Txns db failed to put watchOnly")
	}
}

func TestTxnsGetAll(t *testing.T) {
	var txdb, teardown, err = buildNewTransactionStore()
	if err != nil {
		t.Fatal(err)
	}
	defer teardown()

	tx := wire.NewMsgTx(wire.TxVersion)
	txHex := ""
	raw, _ := hex.DecodeString(txHex)
	r := bytes.NewReader(raw)
	err = tx.Deserialize(r)
	if err != nil {
		t.Log(err)
	}

	err = txdb.Put(raw, tx.TxHash().String(), "1", 5, time.Now(), true)
	if err != nil {
		t.Error(err)
	}
	txns, err := txdb.GetAll(true)
	if err != nil {
		t.Error(err)
	}
	if len(txns) < 1 {
		t.Error("Txns db get all failed")
	}
}

func TestDeleteTxns(t *testing.T) {
	var txdb, teardown, err = buildNewTransactionStore()
	if err != nil {
		t.Fatal(err)
	}
	defer teardown()

	tx := wire.NewMsgTx(wire.TxVersion)
	txHex := ""
	raw, _ := hex.DecodeString(txHex)
	r := bytes.NewReader(raw)
	err = tx.Deserialize(r)
	if err != nil {
		t.Log(err)
	}

	err = txdb.Put(raw, tx.TxHash().String(), "0", 1, time.Now(), false)
	if err != nil {
		t.Error(err)
	}
	txid := tx.TxHash()
	err = txdb.Delete(txid.String())
	if err != nil {
		t.Error(err)
	}
	txns, err := txdb.GetAll(true)
	if err != nil {
		t.Error(err)
	}
	for _, txn := range txns {
		if txn.Txid == txid.String() {
			t.Error("Txns db delete failed")
		}
	}
}

func TestTxnsDB_UpdateHeight(t *testing.T) {
	var txdb, teardown, err = buildNewTransactionStore()
	if err != nil {
		t.Fatal(err)
	}
	defer teardown()

	tx := wire.NewMsgTx(wire.TxVersion)
	txHex := ""
	raw, _ := hex.DecodeString(txHex)
	r := bytes.NewReader(raw)
	err = tx.Deserialize(r)
	if err != nil {
		t.Error(err)
	}

	err = txdb.Put(raw, tx.TxHash().String(), "0", 1, time.Now(), false)
	if err != nil {
		t.Error(err)
	}
	err = txdb.UpdateHeight(tx.TxHash().String(), -1, time.Now())
	if err != nil {
		t.Error(err)
	}
	txn, err := txdb.Get(tx.TxHash().String())
	if err != nil {
		t.Error(err)
	}
	if txn.Height != -1 {
		t.Error("Txn db failed to update height")
	}
}
